SELECT products.products_id, products.products_name FROM `products` INNER JOIN `purchase_requisition_details` ON `purchase_requisition_details`.`products_id` = `products`.`products_id` INNER JOIN `purchase_requisitions` ON `purchase_requisitions`.`purchase_requisitions_id` = `purchase_requisition_details`.`purchase_requisitions_id` WHERE `purchase_requisitions`.`requisition_status` = 4 AND `products`.`status` = 'Active'AND `purchase_requisition_details`.`purchase_cs_id` IS NULL GROUP BY products.products_id